class TransactionData {
  proofBlob: string
  receiptRoot: string
  optionalData?: string[]

  constructor(proofBlob: string, receiptRoot: string, optionalData?: string[]) {
    this.proofBlob = proofBlob
    this.receiptRoot = receiptRoot
    this.optionalData = optionalData
  }
}

export const INVALID_STATUS_TRANSACTION: TransactionData = new TransactionData(
  '',
  '0x8619f1112d159672b84da1e919bfbbcd4935bd897a5b1274bd2a03dae431fa02'
)
